 <?php     $esport = new Esport();     $ImageName = $tournamentinfo['Image'];     $first = substr($ImageName, 0,1);     $second = substr($ImageName, 1,1);?><a href="#"><img style="width:400px;" src="<?php echo MainHelper::site_url('global/pub_img/esport/'.$first.'/'.$second.'/'.$ImageName);?>"/></a><div id="cuptree">    <?php $round = 1;    $match = 1;    $matches = $tournamentinfo['LeagueSize'] / 2;    $first = true;?><?php foreach($tournamentmatches as $matches): ?>    <?php if($round != $matches->Round){ echo '</div>'; $first=true; $extraline =true; }?>    <?php if($first == true){ echo '<div class="cuptree_roundcontainer leftside">'; $first=false;}?>    <?php $round = $matches->Round; ?>                    <?php            if($round == 2){                echo '<div class="cuptree_teamcontainer none_display"></div>';            }            if($round == 3){                if($extraline == true){ echo '<div class="cuptree_teamcontainer none_display"></div>'; $extraline=false; };                $i = 1;                while($i <= 2){                    echo '<div class="cuptree_teamcontainer none_display"></div>';                    $i++;                }            }            if($round == 4){                $i = 1;                while($i <= 5){                    echo '<div class="cuptree_teamcontainer none_display"></div>';                    $i++;                }            }            if($round == 5){                $i = 1;                while($i <= 9){                    echo '<div class="cuptree_teamcontainer none_display"></div>';                    $i++;                }            }            ?>                        <div class="cuptree_teamcontainer">                <span class="seeds"><?php echo $matches->SeedP1 != 0 ? $matches->SeedP1 : ''; ?></span>                <span class="teamname"><?php echo $matches->getP1Name(); ?></span>                <span class="result"><?php echo $matches->RoundWinsP1; ?></span>            </div>            <div class="cuptree_teamcontainer">                <span class="seeds"><?php echo $matches->SeedP2 != 0 ? $matches->SeedP2 : ''; ?></span>                <span class="teamname"><?php echo $matches->getP2Name(); ?></span>                <span class="result"><?php echo $matches->RoundWinsP2; ?></span>            </div>            <?php                if($round == 2 || $round == 4){                    echo '<div class="cuptree_teamcontainer none_display"></div>';                }            ?><?php endforeach; ?>            <?php echo '</div>' ?>            <div class="cuptree_roundcontainer winner">            <div class="cuptree_winner">                <span class="teamname"><?php echo 'Falaffel' ?></span>            </div>            </div></div><?php //echo print_r($tournamentmatches); ?>